import React, { useState } from 'react'
import s from './HW11.module.css'
import s2 from '../../s1-main/App.module.css'
import { restoreState } from '../hw06/localStorage/localStorage'
import SuperRange from './common/c7-SuperRange/SuperRange'

function HW11() {
    // Restore values from localStorage or default to 0 and 100
    const [value1, setValue1] = useState(restoreState<number>('hw11-value1', 0))
    const [value2, setValue2] = useState(restoreState<number>('hw11-value2', 100))

    // Handle value changes for both single and double sliders
    const change = (event: any, value: number | number[]) => {
        if (Array.isArray(value)) {
            // If it's an array, it's the double slider
            setValue1(value[0])
            setValue2(value[1])
        } else {
            // Single slider: Update only value1
            setValue1(value)
        }
    }

    return (
        <div id={'hw11'}>
            <div className={s2.hwTitle}>Homework #11</div>

            <div className={s2.hw}>
                <div className={s.container}>
                    {/* Single Slider */}
                    <div className={s.wrapper}>
                        <span id={'hw11-value'} className={s.number}>{value1}</span>
                        <SuperRange
                            id={'hw11-single-slider'}
                            value={value1}  // Set the value for the single slider
                            onChange={change}  // Handle the change
                            min={0}
                            max={100}
                        />
                    </div>

                    {/* Double Slider */}
                    <div className={s.wrapper}>
                        <span id={'hw11-value-1'} className={s.number}>{value1}</span>
                        <SuperRange
                            id={'hw11-double-slider'}
                            value={[value1, value2]}  // Set the value for the double slider
                            onChange={change}  // Handle the change
                            min={0}
                            max={100}
                            valueLabelDisplay="auto"
                            valueLabelFormat={(value) => `${value}%`}  // Format the value label
                        />
                        <span id={'hw11-value-2'} className={s.number}>{value2}</span>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default HW11
